import React, { useState } from "react";
import { useForm } from "react-hook-form";
import "./App.css";

import data from "./data/data.json";

function App() {
  const [person, setPerson] = useState<string>("");
  const { handleSubmit } = useForm();

  const onSubmit = () => renderDoctor();

  const handleChange = (ev: any) => {
    const value = ev.target.value;

    setPerson(value);
  };

  const renderDoctor = () => {
    const doctorFind = data.find((element) => {
      return element.dni.toLowerCase().includes(person.toLowerCase());
    });
    console.log(doctorFind);
    if (doctorFind) {
      return (
        <li className="listCharacter__list" key={doctorFind.id}>
          <h3>{doctorFind.name}</h3>
        </li>
      );
    } else {
      return <p>Pon DNI</p>;
    }
  };

  const dniPersons = data.map((element: any, id: number) => {
    return (
      <li className="listCharacter__list" key={id}>
        <h3>{element.name}</h3>
      </li>
    );
  });

  return (
    <div className="App">
      <header className="App-header"></header>
      <main>
        <form onSubmit={handleSubmit(onSubmit)}>
          <input type="text" value={person} onChange={handleChange} />
          <input type="submit" />
        </form>
        <ul>{dniPersons}</ul>
      </main>
    </div>
  );
}

export default App;
