import React, { useState } from "react";
import "./App.css";

import data from "./data/data.json";
import logo from "../public/logo.png";

function App() {
  const [searchInput, setSearchInput] = useState("");
  const [foundPerson, setFoundPerson] = useState<any>([
    {
      id: "",
      name: "",
      dni: "",
    },
  ]);

  const doctorSearch = () => {
    if (searchInput == null) return;
    const foundCristi = data.filter((user) => {
      return user.dni.toLowerCase().includes(searchInput.toLowerCase());
    });

    setFoundPerson(foundCristi);
  };

  return (
    <div className="App">
      <header className="App-header">
        <img alt="logo-bu" src={logo}></img>
      </header>
      <main>
        <form>
          <input
            type="search"
            onChange={(e) => setSearchInput(e.target.value)}
          />
          <button type="button" onClick={() => doctorSearch()}>
            Search
          </button>
        </form>
        <ul>
          {foundPerson.map((person: any, index: number) => (
            <li key={index}>{person.name}</li>
          ))}
        </ul>
      </main>
    </div>
  );
}

export default App;
