import React, { useState } from "react";
import { useForm } from "react-hook-form";
import "./App.css";

import data from "./data/data.json";

function App() {
  const [data, setData] = useState<any>([]);
  const [person, setPerson] = useState<string>("");
  const { handleSubmit } = useForm();

  const onSubmit = (data: any) => handleChange(data);

  const handleChange = (ev: any) => {
    const value = ev.target.value;

    setPerson(value);
  };

  const filterPerson = data.filter((user: any) => {
    return user.name.toLowerCase().includes(person.toLowerCase());
  });

  if (data.length === 0) {
    return "not found";
  }

  const dniPersons = data.map((element: any, id: number) => {
    return (
      <li className="listCharacter__list" key={id}>
        <h3>{element.name}</h3>
      </li>
    );
  });
  return (
    <div className="App">
      <header className="App-header"></header>
      <main>
        <form onSubmit={handleSubmit(onSubmit)}>
          <input type="text" value={person} onClick={handleChange} />
          <input type="submit" />
        </form>
      </main>
    </div>
  );
}

export default App;
